import{isFieldNotEmpty}from"./container/UpdateComponent.js";import{TaskComponent}from"./container/TaskComponent.js";function addTask(){let task=document.querySelector("#textbox"),date=document.querySelector("#date");return new Promise((resolve,reject)=>{isFieldNotEmpty(task)&&isFieldNotEmpty(date)&&(resolve({message:"Task added successfully!",task:task.value,date:date.value}),task.value="",date.value=""),reject("Please fill up the required fields.")})}function showMessage(message,color){let successMessage=document.querySelector("#adding-message");successMessage.style.color=color,successMessage.textContent=message,successMessage.style.display="block"}function hideMessage(){let successMessage;document.querySelector("#adding-message").style.display="none"}async function createTask(){try{let ul=document.querySelector("#tasks"),{message:message,task:task,date:date}=await addTask();showMessage(message,"#5cb85c"),console.log(TaskComponent(task,date)),ul.appendChild(TaskComponent(task,date)),setTimeout(()=>{hideMessage()},2e3)}catch(err){showMessage(err,"#d9534f")}}document.querySelector("#add-button").addEventListener("click",()=>{createTask()});